import React, { useState } from 'react';
import { Palette, Check } from 'lucide-react';

const ColorPicker = ({ currentColor, onColorChange }) => {
  const [isOpen, setIsOpen] = useState(false);

  const colors = [
    { id: 'purple', name: 'Purple', class: 'bg-accent-purple-500', hex: '#a855f7' },
    { id: 'blue', name: 'Blue', class: 'bg-accent-blue-500', hex: '#3b82f6' },
    { id: 'green', name: 'Green', class: 'bg-accent-green-500', hex: '#22c55e' },
    { id: 'orange', name: 'Orange', class: 'bg-accent-orange-500', hex: '#f97316' },
    { id: 'pink', name: 'Pink', class: 'bg-accent-pink-500', hex: '#ec4899' }
  ];

  const currentColorData = colors.find(c => c.id === currentColor) || colors[0];

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-2 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600 transition-all duration-200 hover:scale-105 flex items-center gap-2"
        title="Change Accent Color"
      >
        <Palette size={20} />
        <div 
          className="w-4 h-4 rounded-full border-2 border-white dark:border-gray-800 shadow-sm"
          style={{ backgroundColor: currentColorData.hex }}
        ></div>
      </button>

      {isOpen && (
        <>
          {/* Backdrop */}
          <div 
            className="fixed inset-0 z-10" 
            onClick={() => setIsOpen(false)}
          ></div>
          
          {/* Color Picker Dropdown */}
          <div className="absolute right-0 top-full mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-20 animate-slide-in">
            <div className="p-3">
              <h3 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                Choose Accent Color
              </h3>
              
              <div className="grid grid-cols-5 gap-2">
                {colors.map((color) => (
                  <button
                    key={color.id}
                    onClick={() => {
                      onColorChange(color.id);
                      setIsOpen(false);
                    }}
                    className={`relative p-3 rounded-lg transition-all duration-200 hover:scale-110 ${
                      currentColor === color.id 
                        ? 'ring-2 ring-gray-400 dark:ring-gray-500' 
                        : 'hover:ring-2 hover:ring-gray-300 dark:hover:ring-gray-600'
                    }`}
                    title={color.name}
                  >
                    <div 
                      className={`w-8 h-8 rounded-full ${color.class} shadow-sm`}
                    ></div>
                    
                    {currentColor === color.id && (
                      <div className="absolute inset-0 flex items-center justify-center">
                        <Check size={14} className="text-white drop-shadow-lg font-bold" />
                      </div>
                    )}
                  </button>
                ))}
              </div>
              
              <div className="mt-3 pt-3 border-t border-gray-200 dark:border-gray-600">
                <p className="text-xs text-gray-500 dark:text-gray-400 text-center">
                  Current: {currentColorData.name}
                </p>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default ColorPicker;
