import React from 'react';
import { X, Moon, Sun, Palette, Edit3, FolderPlus } from 'lucide-react';

const SettingsModal = ({ 
  isOpen, 
  onClose, 
  isDarkMode, 
  onToggleTheme, 
  accentColor, 
  onColorChange,
  categories,
  onRenameCategory 
}) => {
  // Don't render if modal is not open
  if (!isOpen) return null;

  // Get accent color classes for dynamic styling
  const getAccentClasses = (color) => {
    const colorMap = {
      purple: 'accent-purple',
      blue: 'accent-blue',
      green: 'accent-green',
      orange: 'accent-orange',
      pink: 'accent-pink'
    };
    return colorMap[color] || 'accent-purple';
  };

  const accentClass = getAccentClasses(accentColor);

  // Available accent colors for the picker
  const colors = [
    { id: 'purple', name: 'Purple', class: 'bg-accent-purple-500', hex: '#a855f7' },
    { id: 'blue', name: 'Blue', class: 'bg-accent-blue-500', hex: '#3b82f6' },
    { id: 'green', name: 'Green', class: 'bg-accent-green-500', hex: '#22c55e' },
    { id: 'orange', name: 'Orange', class: 'bg-accent-orange-500', hex: '#f97316' },
    { id: 'pink', name: 'Pink', class: 'bg-accent-pink-500', hex: '#ec4899' }
  ];

  const currentColorData = colors.find(c => c.id === accentColor) || colors[0];

  return (
    <>
      {/* Backdrop - click to close modal */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 z-40 transition-opacity duration-300"
        onClick={onClose}
      ></div>
      
      {/* Modal */}
      <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-2xl max-w-md w-full max-h-[90vh] overflow-y-auto animate-bounce-in">
          {/* Modal Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
            <h2 className="text-xl font-bold text-gray-800 dark:text-gray-200">
              Settings
            </h2>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
            >
              <X size={20} />
            </button>
          </div>

          {/* Modal Content */}
          <div className="p-6 space-y-6">
            {/* Theme Section */}
            <div>
              <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-200 mb-4 flex items-center gap-2">
                <Moon size={20} className="text-gray-600 dark:text-gray-400" />
                Appearance
              </h3>
              
              {/* Dark/Light Mode Toggle */}
              <div className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                <div className="flex items-center gap-3">
                  {isDarkMode ? (
                    <Moon size={20} className="text-yellow-500" />
                  ) : (
                    <Sun size={20} className="text-blue-600" />
                  )}
                  <div>
                    <p className="font-medium text-gray-800 dark:text-gray-200">
                      {isDarkMode ? 'Dark Mode' : 'Light Mode'}
                    </p>
                    <p className="text-sm text-gray-500 dark:text-gray-400">
                      {isDarkMode ? 'Switch to light theme' : 'Switch to dark theme'}
                    </p>
                  </div>
                </div>
                <button
                  onClick={onToggleTheme}
                  className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                    isDarkMode ? 'bg-gray-600' : 'bg-gray-200'
                  }`}
                >
                  <span
                    className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                      isDarkMode ? 'translate-x-6' : 'translate-x-1'
                    }`}
                  />
                </button>
              </div>
            </div>

            {/* Accent Color Section */}
            <div>
              <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-200 mb-4 flex items-center gap-2">
                <Palette size={20} className="text-gray-600 dark:text-gray-400" />
                Accent Color
              </h3>
              
              <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
                  Choose your preferred accent color for buttons and highlights
                </p>
                
                {/* Color Swatches */}
                <div className="grid grid-cols-5 gap-3">
                  {colors.map((color) => (
                    <button
                      key={color.id}
                      onClick={() => onColorChange(color.id)}
                      className={`relative p-3 rounded-lg transition-all duration-200 hover:scale-110 ${
                        accentColor === color.id 
                          ? 'ring-2 ring-gray-400 dark:ring-gray-500 shadow-lg' 
                          : 'hover:ring-2 hover:ring-gray-300 dark:hover:ring-gray-600'
                      }`}
                      title={color.name}
                    >
                      <div 
                        className={`w-8 h-8 rounded-full ${color.class} shadow-sm`}
                      ></div>
                      
                      {accentColor === color.id && (
                        <div className="absolute inset-0 flex items-center justify-center">
                          <Check size={14} className="text-white drop-shadow-lg font-bold" />
                        </div>
                      )}
                    </button>
                  ))}
                </div>
                
                {/* Current Color Display */}
                <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-600">
                  <div className="flex items-center gap-3">
                    <div 
                      className="w-6 h-6 rounded-full border-2 border-white dark:border-gray-800 shadow-sm"
                      style={{ backgroundColor: currentColorData.hex }}
                    ></div>
                    <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      Current: {currentColorData.name}
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {/* Categories Section */}
            <div>
              <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-200 mb-4 flex items-center gap-2">
                <Edit3 size={20} className="text-gray-600 dark:text-gray-400" />
                Categories
              </h3>
              
              <div className="space-y-2">
                {categories.map((category) => (
                  <div 
                    key={category.id}
                    className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  >
                    <div className="flex items-center gap-3">
                      <FolderPlus size={16} className="text-gray-500 dark:text-gray-400" />
                      <span className="text-gray-800 dark:text-gray-200 font-medium">
                        {category.name}
                      </span>
                      <span className="text-xs text-gray-500 dark:text-gray-400 bg-gray-200 dark:bg-gray-600 px-2 py-1 rounded-full">
                        {category.taskCount || 0} tasks
                      </span>
                    </div>
                    <button
                      onClick={() => {
                        const newName = prompt('Enter new category name:', category.name);
                        if (newName && newName.trim() && newName !== category.name) {
                          onRenameCategory(category.id, newName.trim());
                        }
                      }}
                      className="p-1.5 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600 rounded transition-colors"
                      title="Rename Category"
                    >
                      <Edit3 size={14} />
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Modal Footer */}
          <div className="p-6 border-t border-gray-200 dark:border-gray-700">
            <button
              onClick={onClose}
              className={`w-full btn btn-primary-${accentColor}`}
            >
              Done
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default SettingsModal;
